// import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, TextInput, TouchableOpacity ,TouchableHighlight} from 'react-native';
// import { db } from '../config';
import firestore from '@react-native-firebase/firestore';
import firebase from '../config';
import As from './AuthServices';


// let addItem = item => {
//     db.ref('/items').push({
//         name: item
//     });
// };

export default class registration extends React.Component {
    constructor(props) {
        super(props);
        this.buttonPress = this.buttonPress.bind(this);
        this.dbRef = firebase.firestore().collection('users');
    }
    state = {
        username: "",
        email: "",
        password: "",
        contact: "",
        UID: ','
    };
    buttonPress() {


        As.signUp(this.state.email, this.state.password)
            .then((userCredential) => {
                // Signed in
                console.log("successed!!");
                var user = userCredential.user;
                this.dbRef.doc(As.Current().uid).set({
                    UserName: this.state.username,
                    Email: this.state.email,
                    contact: this.state.contact,
                }).then(() => {
                    console.log('User addedd!!!!');
                    this.props.navigation.navigate('loginpg');
                }).catch((error) => {
                    console.log(error);
                    alert(error);
                });
            })
            .catch((error) => {
                console.log(error);
                alert(error);
            });
        console.log(this.state.username + " " + this.state.email + " " + this.state.password + " " + this.state.contact);
    }

    render() {

        const buttonStylesubmit = {
            color: "#FFFFFF",
            backgroundColor: "#008484",
            paddingBottom: "17px",
            paddingTop: "3px",
            paddingRight: "18px",
            paddingLeft: "18px",
            //fontFamily: "calibri",
            fontSize: "35px",
            marginRight: "80px",
            height: "60px",
            width: "350px",
            borderWidth: 6,
            borderColor: "#FFFFFF",
            marginLeft: 55,
            marginBottom: 40
        }

        const register = {
            //fontFamily: "calibri",
            fontWeight: "bold",
            fontSize: 20,
            color: "#008484"
        }

        return (
            <View style={styles.container}>
                <Text style={styles.logo}>P-Indicator</Text>
                <Text style={styles.inputText}>Username</Text>
                <View style={styles.inputView} >
                    <TextInput
                        style={styles.inputText}
                        placeholder="Enter your username..."
                        placeholderTextColor="#e1e1e1"
                        onChangeText={text => this.setState({ username: text })} />
                </View>
                <Text style={styles.inputText}>E-mail Id</Text>
                <View style={styles.inputView} >
                    <TextInput
                        style={styles.inputText}
                        placeholder="Enter your Email..."
                        placeholderTextColor="#e1e1e1"
                        onChangeText={text => this.setState({ email: text })} />
                </View>
                <Text style={styles.inputText}>Password</Text>
                <View style={styles.inputView} >
                    <TextInput
                        secureTextEntry
                        style={styles.inputText}
                        placeholder="Enter your Password..."
                        placeholderTextColor="#e1e1e1"
                        onChangeText={text => this.setState({ password: text })} />
                </View>
                <Text style={styles.inputText}>Contact No</Text>
                <View style={styles.inputView} >
                    <TextInput
                        style={styles.inputText}
                        placeholder="Enter your Contant number..."
                        placeholderTextColor="#e1e1e1"
                        onChangeText={text => this.setState({ phoneNo: text })} />
                </View>
                <View>
                    {/* <button style={buttonStylesubmit} onClick={this.buttonPress}>Register</button> */}
                    <TouchableHighlight
                    style={styles.button}
                    underlayColor="white"
                    onPress={this.buttonlogin}
                >
                    <Text style={styles.buttonText}>Register</Text>
                </TouchableHighlight>
                </View>
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#008484",
        alignItems: 'center',
        justifyContent: 'center',
    },
    logo: {
        fontWeight: "bold",
        fontSize: 50,
        //fontFamily: "calibri",
        color: "#ffffff",
        // fontStyle:"italic",
        marginBottom: 40
    },
    inputView: {
        width: "75%",
        backgroundColor: "#ffffff",
        height: 40,
        marginBottom: 20,
        marginTop: 10,
        justifyContent: "center",
        padding: 10
    },
    inputText: {
        height: 20,
        //fontFamily: "calibri",
        fontSize: 16,
        marginRight: 250,
        fontStyle: "italic",
        color: "#ffffff",
        fontWeight: "bold",
    },
    registerBtn: {
        width: "20%",
        backgroundColor: "#e1e1e1",
        borderRadius: 10,
        height: 40,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 20,
        marginBottom: 30
    },
    buttonText: {
        fontSize: 20,
        fontWeight: "bold",
        color: '#111',
        alignSelf: 'center',
        color: '#008484',
        alignItems: "center",
        justifyContent: "center",
        marginRight: 45,
    },
    button: {
        height: 45,
        alignItems: "center",
        justifyContent: "center",
        width:"50%",
        flexDirection: 'row',
        backgroundColor: '#ffffff',
        borderColor: 'white',
        borderWidth: 1,
        borderRadius: 8,
        marginBottom: 10,
        marginTop: 40,
        borderWidth: 6,
        borderColor: "#808080",
        color: '#008484',
    },
});
