import { StatusBar } from 'expo-status-bar';
import React from 'react';
import {  StyleSheet, Text, View } from 'react-native';
import firebase from '../config';

export default class Slot extends React.Component {

    state = {
        dur:this.props.navigation.state.params.p1,
        time: this.props.navigation.state.params.p2 + ": " + this.props.navigation.state.params.p3 + "  " + this.props.navigation.state.params.p4,
        vehi: this.props.navigation.state.params.p5,
        slot: '',
        amount:'',
        slot1: '',
        slot2: '',
        slot3: '',
        slot4: '',
        slot5: '',
        slot6: '',
        slot7: '',
        slot8: '',
        slot9: '',
        slot10: '',
        // colo:'',
    };

    Slot1 = () => {
        this.setState({
            slot: '1',
            slot1:true,
            // colo:'#000000',
        });
    }
    Slot2 = () => {
        this.setState({
            slot: '2',
            slot2: true,
        });
    }
    Slot3 = () => {
        this.setState({
            slot: '3',
            slot3: true,
        });
    }

    Slot4 = () => {
        this.setState({
            slot: '4',
            slot4: true,
        });
    }
    Slot5 = () => {
        this.setState({
            slot: '5',
            slot5: true,
        });
    }
    Slot6 = () => {
        this.setState({
            slot: '6',
            slot6: true,
        });
    }
    Slot7 = () => {
        this.setState({
            slot: '7',
            slot7: true,
        });
    }
    Slot8 = () => {
        this.setState({
            slot: '8',
            slot8: true,
        });
    }
    Slot9 = () => {
        this.setState({
            slot: '9',
            slot9: true,
        });
    }
    Slot10 = () => {
        this.setState({
            slot: '10',
            slot10: true,
        });
    }

    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.dbRef = firebase.firestore().collection('BookingDet');
    }


    handleSubmit = () => {
        console.log('Item saved successfully');
        if (this.state.vehi ==='Car')
        {
            this.state.amount = 50 * parseInt(this.state.dur);
        }
        else
        {
            this.state.amount = 30 * parseInt(this.state.dur);
        }

        console.log(this.state.dur + "  and  " + this.state.time + "  and " + this.state.vehi + " and " + this.state.amount);

        this.dbRef.add({
            Duration: this.state.dur,
            Start: this.state.time,
            TypeOFVehi: this.state.vehi,
            SlotNo: this.state.slot,
            Paid: this.state.amount,
        }).then(() => {
            console.log('Item saved successfully');
            this.props.navigation.navigate('pay',{a1:this.state.amount});
        })
            .catch((err) => {
                console.error("Error found: ", err);
            });
    };

    render() {
       
        const buttonStyle= {
            color: "#008484",
            backgroundColor: "#FFFFFF",
            // backgroundColor: this.state.colo,
            padding: 22,
            paddingTop: 10,
            fontFamily: "calibri",
            fontSize: 30,
            marginRight: 80,
            height: 60,
            width: 160,
            borderWidth: 6,
            borderColor: "#FFFFFF"
        }
        const buttonStyleten = {
            color: "#008484",
            backgroundColor: "#FFFFFF",
            padding: 17,
            paddingTop: 10,
            paddingRight: 18,
            paddingLeft: 18,
            fontFamily: "calibri",
            fontSize: 30,
            marginRight: 80,
            height: 60,
            width: 160,
            borderWidth: 6,
            borderColor: "#FFFFFF"
        }
        const buttonStyleentry = {
            color: "#008484",
            backgroundColor: "#FFFFFF",
            padding: 17,
            paddingTop: 10,
            paddingRight: 18,
            paddingLeft: 18,
            fontFamily: "calibri",
            fontSize: 25,
            marginRight: 80,
            height: 60,
            width: 200,
        }
        const buttonStyleGo = {
            color: "#FFFFFF",
            backgroundColor: "#008484",
            paddingBottom: 17,
            paddingTop: 3,
            paddingRight: 18,
            paddingLeft: 18,
            fontFamily: "calibri",
            fontSize: 35,
            marginRight: 80,
            height: 60,
            width: 150,
            borderWidth: 6,
            borderColor: "#FFFFFF",
        }
        const selectText = {
            color: "#FFFFFF",
            fontFamily: "calibri",
            justifyContent: 'center',
            fontSize: 40,
            marginRight: 65
        }
        return (
            <View style={styles.container}>
                <View style={styles.rowContainer}>
                    <Text style={selectText}>Select Your Slot</Text>
                </View>
                <View style={styles.rowContainer}>
                    <button style={buttonStyle} disabled={this.state.slot1} onClick={this.Slot1}>Slot 1</button>
                    <button style={buttonStyle} disabled={this.state.slot2} onClick={this.Slot2}>Slot 2</button>
                </View>
                {/* disabled="true" */}
                <View style={styles.rowContainer}>
                    <button style={buttonStyle} disabled={this.state.slot3} onClick={this.Slot3}>Slot 3</button>
                    <button style={buttonStyle} disabled={this.state.slot4} onClick={this.Slot4}>Slot 4</button>
                </View>
                <View style={styles.rowContainer}>
                    <button style={buttonStyle} disabled={this.state.slot5} onClick={this.Slot5}>Slot 5</button>
                    <button style={buttonStyle} disabled={this.state.Slot6} onClick={this.Slot6}>Slot 6</button>
                </View>
                <View style={styles.rowContainer}>
                    <button style={buttonStyle} disabled={this.state.slot7} onClick={this.Slot7}>Slot 7</button>
                    <button style={buttonStyle} disabled={this.state.slot8} onClick={this.Slot8}>Slot 8</button>
                </View>
                <View style={styles.rowContainer}>
                    <button style={buttonStyle} disabled={this.state.slot9} onClick={this.Slot9}>Slot 9</button>
                    <button style={buttonStyleten} disabled={this.state.slot10} onClick={this.Slot10} >Slot 10</button>
                </View>
                <View style={styles.rowContainer}>
                    <button style={buttonStyleentry} disabled="true">Entry Gate</button>
                </View>
                {/* <Text>
                    hey {dur}
                </Text> */}
                <View style={styles.rowContainer}>
                    <button style={buttonStyleGo} onClick={this.handleSubmit}>GO</button>
                </View>
                <StatusBar style="auto" />
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#008484',
        alignItems: 'center',
        justifyContent: 'center'
    },
    rowContainer: {
        margin: 10,
        flex: 1,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 2
    },
    buttonText :{
        color: "#008484",
        backgroundColor: "#FFFFFF",
        fontFamily: "calibri",
        fontSize: 30,
        borderColor: "#FFFFFF"
    }

});
