//yarn add @react-navigation/native
//yarn add react-native-reanimated react-native-gesture-handler react-native-screens react-native-safe-area-context @react-native-community/masked-view
//yarn add react-native-webview
//npm install util
import * as React from 'react';
import { View, Button, Text, Animated, StyleSheet, TouchableHighlight } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { WebView } from 'react-native-webview';
const util = require('util');
const userArr=[];
var userA;
function componentDidMount() {
    this.unsubscribe = firebase.firestore().collection('BookingDet').onSnapshot(this.getCollection);
}
state = {
    Amount:'',
}
getCollection = (querySnapshot) => {
    querySnapshot.forEach((res) => {
        const { Paid,UID } = res.data();
        userArr.push({
            Paid,UID
        });
    });
    for (i = 0; i < userArr.length; i++) {
        if (userArr[i].UID === As.Current().uid) {
            userA= userArr[i].Paid;
        }
    }

    console.log(userA);
}
function Home({ navigation }) {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center',backgroundColor: "#008484" }}>
    <Text style={{fontWeight:"bold",fontSize:30,color:"#e1e1e1",marginBottom:30}}>P-Indicator Payment</Text>
    <Text style={{fontSize:20,color:"#e1e1e1"}}> Total Parking charges for each slot is </Text>
    <Text style={{fontSize:30,color:"#e1e1e1",fontWeight:"bold"}}>₹50</Text>
    <Text></Text><Text></Text><Text></Text><Text></Text><Text></Text>
    {/* <Button >
        color="#FFFFFF",
        <View style={styles.pay}>Pay Now</View>
        onPress={() => navigation.navigate('Razor Pay')}
        </Button> */}
    <View>
    <TouchableHighlight
                    style={styles.Button}
                    underlayColor="white"
                    onPress={() => navigation.navigate('Razor Pay')}
                >
                    <Text style={styles.pay}>Pay Now</Text>
                </TouchableHighlight></View>
    </View>
  );
  }

function Profile({ navigation }) {
    return <WebView source={{ uri: 'https://rzp.io/l/OqHNiG5lx3' }} />;
}

const forFade = ({ current, next }) => {
  const opacity = Animated.add(
    current.progress,
    next ? next.progress : 0
  ).interpolate({
    inputRange: [0, 1, 2],
    outputRange: [0, 1, 0],
  });

  return {
    leftButtonStyle: { opacity },
    rightButtonStyle: { opacity },
    titleStyle: { opacity },
    backgroundStyle: { opacity },
  };
};

const Stack = createStackNavigator();

function MyStack() {
  return (
    <Stack.Navigator>
      <Stack.Screen
        name="P-Indicator Payment"
        component={Home}
        options={{
          headerTintColor: 'white',
          headerStyle: { backgroundColor: '#008484' },
        }}
      />
      <Stack.Screen
        name="Razor Pay"
        component={Profile}
        options={{headerTintColor: 'white', headerStyle: { backgroundColor: '#008484' },headerStyleInterpolator: forFade }}
      />
    </Stack.Navigator>
  );
}
const styles = StyleSheet.create({
  pay:{
    fontSize:20,
    color:"#008484",
    justifyContent: "center",
    alignItems: "center",
    marginRight:25,
    fontWeight: "bold",
  },
  Button: {
    height: 45,
    alignItems: "center",
    justifyContent: "center",
    width:"35%",
    flexDirection: 'row',
    backgroundColor: '#ffffff',
    borderColor: 'white',
    borderWidth: 1,
    borderRadius: 8,
    marginBottom: 10,
    marginTop: 20,
    borderWidth: 6,
    borderColor: "#FFFFFF",
    color: '#008484',
},
})

export default function App() {
  return (
    <NavigationContainer>
      <MyStack />
    </NavigationContainer>
  );
}
